import arcpy
input_fc = r"C:\Data\facilities.shp" distances = [100, 250, 500] # meters
for distance in distances: output_fc = rf"C:\Data\output.gdb\buffer_{distance}m" arcpy.yamlanalysistoolbox.BufferAnalysis( input_fc, distance, "meters", True, output_fc ) print(f"Created {distance}m buffer: {output_fc}")
